import React, { useState } from "react";

const Dashboard = () => {
  const [formData, setFormData] = useState({
    skills: "",
    interests: "",
    experience: "",
  });

  const [careerSuggestions, setCareerSuggestions] = useState([]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Dummy suggestions (Replace with AI logic/API call later)
    const dummyOutput = [
      "Software Developer",
      "AI/ML Engineer",
      "Data Analyst",
    ];

    setCareerSuggestions(dummyOutput);
  };

  return (
    <div style={styles.page}>
      <div style={styles.container}>
        <h2>Welcome to Career Dashboard</h2>
        <p>Get personalized career suggestions based on your profile</p>

        <form onSubmit={handleSubmit} style={styles.form}>
          <textarea
            name="skills"
            placeholder="Enter your skills (e.g., Python, React)"
            value={formData.skills}
            onChange={handleChange}
            style={styles.input}
            required
          />
          <textarea
            name="interests"
            placeholder="Your interests (e.g., AI, Web Development)"
            value={formData.interests}
            onChange={handleChange}
            style={styles.input}
            required
          />
          <textarea
            name="experience"
            placeholder="Your experience (e.g., 1 year internship at XYZ)"
            value={formData.experience}
            onChange={handleChange}
            style={styles.input}
            required
          />
          <button type="submit" style={styles.button}>Get Career Suggestions</button>
        </form>

        {careerSuggestions.length > 0 && (
          <div style={styles.suggestions}>
            <h3>Suggested Career Paths:</h3>
            <ul>
              {careerSuggestions.map((career, idx) => (
                <li key={idx}>{career}</li>
              ))}
            </ul>
          </div>
        )}

        <div style={styles.features}>
          <button style={styles.featureBtn}>Generate Resume</button>
          <button style={styles.featureBtn}>Mock Interview</button>
          <button style={styles.featureBtn}>Skill Gap Analysis</button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  page: {
    background: "linear-gradient(to right, #2c3e50, #3498db)",
    minHeight: "100vh",
    color: "#fff",
    padding: "40px",
  },
  container: {
    background: "#fff",
    color: "#333",
    padding: "30px",
    borderRadius: "10px",
    maxWidth: "700px",
    margin: "auto",
    boxShadow: "0 8px 20px rgba(0, 0, 0, 0.3)",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "15px",
    marginTop: "20px",
  },
  input: {
    height: "80px",
    padding: "10px",
    fontSize: "16px",
    borderRadius: "5px",
    border: "1px solid #ccc",
    resize: "vertical",
  },
  button: {
    padding: "10px",
    backgroundColor: "#3498db",
    color: "#fff",
    fontWeight: "bold",
    fontSize: "16px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  suggestions: {
    marginTop: "30px",
    backgroundColor: "#f4f4f4",
    padding: "20px",
    borderRadius: "8px",
  },
  features: {
    marginTop: "30px",
    display: "flex",
    gap: "15px",
    flexWrap: "wrap",
  },
  featureBtn: {
    flex: "1 1 30%",
    padding: "12px",
    backgroundColor: "#2ecc71",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    fontWeight: "600",
    cursor: "pointer",
  },
};

export default Dashboard;
