import React, { useState } from "react";

const LoginRegister = () => {
  const [isLogin, setIsLogin] = useState(true);

  const toggleForm = () => setIsLogin(!isLogin);

  return (
    <div style={styles.container}>
      <style>
        {`
          body {
            margin: 0;
            font-family: 'Segoe UI', sans-serif;
            background: linear-gradient(to right, #667eea, #764ba2);
          }
          input, button {
            font-size: 16px;
          }
          input:focus, button:focus {
            outline: none;
          }
        `}
      </style>
      <div style={styles.box}>
        <h2 style={styles.heading}>{isLogin ? "Login" : "Register"}</h2>
        <form style={styles.form}>
          <input type="text" placeholder="Username" required style={styles.input} />
          {!isLogin && <input type="email" placeholder="Email" required style={styles.input} />}
          <input type="password" placeholder="Password" required style={styles.input} />
          {!isLogin && (
            <input type="password" placeholder="Confirm Password" required style={styles.input} />
          )}
          <button type="submit" style={styles.button}>
            {isLogin ? "Login" : "Register"}
          </button>
        </form>
        <p onClick={toggleForm} style={styles.toggle}>
          {isLogin ? "Don't have an account? Register" : "Already have an account? Login"}
        </p>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    height: "100vh",
    justifyContent: "center",
    alignItems: "center",
    background: "#f0f4f8",
  },
  box: {
    width: "350px",
    padding: "30px",
    borderRadius: "10px",
    backgroundColor: "#ffffff",
    boxShadow: "0 10px 30px rgba(0, 0, 0, 0.1)",
    textAlign: "center",
  },
  heading: {
    marginBottom: "20px",
    color: "#333",
  },
  form: {
    display: "flex",
    flexDirection: "column",
  },
  input: {
    marginBottom: "15px",
    padding: "10px",
    border: "1px solid #ccc",
    borderRadius: "5px",
  },
  button: {
    padding: "10px",
    backgroundColor: "#667eea",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background 0.3s",
  },
  toggle: {
    marginTop: "15px",
    color: "#667eea",
    cursor: "pointer",
    fontSize: "14px",
  },
};

export default LoginRegister;
