import React, { useState } from "react";

const Dashboard = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    degree: "",
    university: "",
    graduationYear: "",
    skills: "",
    interests: "",
    experience: "",
    certifications: "",
    careerGoals: "",
  });

  const [profileImage, setProfileImage] = useState(null);
  const [certificateFile, setCertificateFile] = useState(null);
  const [careerSuggestions, setCareerSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleProfileImageChange = (e) => {
    setProfileImage(e.target.files[0]);
  };

  const handleCertificateFileChange = (e) => {
    setCertificateFile(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const data = new FormData();
    Object.entries(formData).forEach(([key, value]) => {
      data.append(key, value);
    });

    if (profileImage) data.append("profileImage", profileImage);
    if (certificateFile) data.append("certificateFile", certificateFile);

    // Simulated response
    setCareerSuggestions([
      "Software Developer",
      "AI/ML Engineer",
      "UI/UX Designer",
      "Cybersecurity Analyst",
    ]);
    setShowSuggestions(true);
  };

  const handleAIResume = () => {
    alert("Generating AI Resume... (you can connect backend here)");
  };

  const handleMockInterview = () => {
    alert("Starting mock interview... (add feature logic here)");
  };

  const handleSkillGap = () => {
    alert("Analyzing skill gaps... (connect to ML model or logic)");
  };

  return (
    <div style={styles.page}>
      <div style={styles.container}>
        <h2>Smart Career Guidance & Resume Platform</h2>
        <p>Fill your details to get personalized career suggestions and resume tips.</p>

        {/* 📄 Form */}
        <form onSubmit={handleSubmit} style={styles.form} encType="multipart/form-data">
          <input type="text" name="fullName" placeholder="Full Name" onChange={handleChange} required style={styles.input} />
          <input type="email" name="email" placeholder="Email" onChange={handleChange} required style={styles.input} />
          <input type="text" name="degree" placeholder="Degree (e.g., B.Tech)" onChange={handleChange} required style={styles.input} />
          <input type="text" name="university" placeholder="University / College" onChange={handleChange} required style={styles.input} />
          <input type="number" name="graduationYear" placeholder="Graduation Year" onChange={handleChange} required style={styles.input} />
          <textarea name="skills" placeholder="Skills" onChange={handleChange} required style={styles.textarea} />
          <textarea name="interests" placeholder="Interests" onChange={handleChange} required style={styles.textarea} />
          <textarea name="experience" placeholder="Experience / Projects" onChange={handleChange} required style={styles.textarea} />
          <textarea name="certifications" placeholder="Certifications" onChange={handleChange} style={styles.textarea} />
          <textarea name="careerGoals" placeholder="Career Goals" onChange={handleChange} style={styles.textarea} />

          <label style={styles.label}>Upload Profile Image:</label>
          <input type="file" accept="image/*" onChange={handleProfileImageChange} style={styles.fileInput} />
          <label style={styles.label}>Upload Certificate (PDF):</label>
          <input type="file" accept=".pdf" onChange={handleCertificateFileChange} style={styles.fileInput} />

          <button type="submit" style={styles.button}>Get Career Suggestions</button>
        </form>

        {/* 🎯 Suggestions Section */}
        {showSuggestions && (
          <div style={styles.suggestions}>
            <h3>Suggested Career Paths:</h3>
            <ul>
              {careerSuggestions.map((career, index) => (
                <li key={index}>{career}</li>
              ))}
            </ul>
          </div>
        )}

        {/* ✅ Green Feature Buttons BELOW Suggestions */}
        {showSuggestions && (
          <div style={styles.greenOptions}>
            <button onClick={handleAIResume} style={styles.greenBtn}>Generate AI Resume</button>
            <button onClick={handleMockInterview} style={styles.greenBtn}>Mock Interview</button>
            <button onClick={handleSkillGap} style={styles.greenBtn}>Skill Gap Analysis</button>
          </div>
        )}
      </div>
    </div>
  );
};

const styles = {
  page: {
    background: "linear-gradient(to right, #a8edea, #fed6e3)",
    minHeight: "100vh",
    padding: "40px 20px",
    color: "#333",
  },
  container: {
    backgroundColor: "#fff",
    padding: "30px",
    borderRadius: "10px",
    maxWidth: "800px",
    margin: "auto",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "12px",
  },
  input: {
    padding: "12px",
    fontSize: "16px",
    borderRadius: "6px",
    border: "1px solid #ccc",
  },
  textarea: {
    padding: "12px",
    fontSize: "16px",
    borderRadius: "6px",
    border: "1px solid #ccc",
    resize: "vertical",
    minHeight: "60px",
  },
  label: {
    marginTop: "10px",
    fontWeight: "bold",
  },
  fileInput: {
    padding: "6px 0",
  },
  button: {
    padding: "14px",
    backgroundColor: "#2563eb",
    color: "#fff",
    fontWeight: "bold",
    fontSize: "16px",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    marginTop: "20px",
  },
  suggestions: {
    marginTop: "30px",
    background: "#f0fdf4",
    padding: "20px",
    borderRadius: "8px",
  },
  greenOptions: {
    display: "flex",
    gap: "15px",
    justifyContent: "center",
    marginTop: "30px",
    flexWrap: "wrap",
  },
  greenBtn: {
    padding: "10px 18px",
    backgroundColor: "#22c55e",
    color: "#fff",
    fontWeight: "600",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
  },
};

export default Dashboard;
