import React, { useState } from "react";

const Dashboard = () => {
  // State to hold form inputs
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    degree: "",
    university: "",
    graduationYear: "",
    skills: "",
    interests: "",
    experience: "",
    certifications: "",
    careerGoals: "",
  });

  // File states
  const [profileImage, setProfileImage] = useState(null);
  const [certificateFile, setCertificateFile] = useState(null);

  // Suggestions state
  const [careerSuggestions, setCareerSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);

  // Handle input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle file changes
  const handleProfileImageChange = (e) => {
    setProfileImage(e.target.files[0]);
  };
  const handleCertificateFileChange = (e) => {
    setCertificateFile(e.target.files[0]);
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // In real app, send `formData`, `profileImage`, and `certificateFile` to backend here

    alert("ðŸŽ¯ Career suggestions generated based on your profile.");

    // Dummy career suggestions
    setCareerSuggestions([
      "Software Developer",
      "AI/ML Engineer",
      "UI/UX Designer",
      "Cybersecurity Analyst",
    ]);
    setShowSuggestions(true);
  };

  // Button action handlers
  const generateResume = () => {
    alert("ðŸ“„ Generating AI-powered resume...");
    console.log("AI resume generation started...");
  };

  const startMockInterview = () => {
    alert("ðŸŽ™ï¸ Starting mock interview session...");
    console.log("Mock interview started...");
  };

  const analyzeSkillGap = () => {
    alert("ðŸ“Š Analyzing skill gaps...");
    console.log("Skill gap analysis in progress...");
  };

  return (
    <div style={styles.page}>
      <div style={styles.container}>
        <h2>Smart Career Guidance & Resume Platform</h2>
        <p>Fill your details to get personalized career suggestions and resume tips.</p>

        {/* Form */}
        <form onSubmit={handleSubmit} style={styles.form} encType="multipart/form-data">
          <input
            type="text"
            name="fullName"
            placeholder="Full Name"
            value={formData.fullName}
            onChange={handleChange}
            required
            style={styles.input}
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
            style={styles.input}
          />
          <input
            type="text"
            name="degree"
            placeholder="Degree (e.g., B.Tech)"
            value={formData.degree}
            onChange={handleChange}
            required
            style={styles.input}
          />
          <input
            type="text"
            name="university"
            placeholder="University / College"
            value={formData.university}
            onChange={handleChange}
            required
            style={styles.input}
          />
          <input
            type="number"
            name="graduationYear"
            placeholder="Graduation Year"
            value={formData.graduationYear}
            onChange={handleChange}
            required
            style={styles.input}
          />
          <textarea
            name="skills"
            placeholder="Skills (e.g., JavaScript, Python)"
            value={formData.skills}
            onChange={handleChange}
            required
            style={styles.textarea}
          />
          <textarea
            name="interests"
            placeholder="Interests (e.g., Web Dev, AI)"
            value={formData.interests}
            onChange={handleChange}
            required
            style={styles.textarea}
          />
          <textarea
            name="experience"
            placeholder="Experience / Projects"
            value={formData.experience}
            onChange={handleChange}
            required
            style={styles.textarea}
          />
          <textarea
            name="certifications"
            placeholder="Certifications (AWS, Coursera etc.)"
            value={formData.certifications}
            onChange={handleChange}
            style={styles.textarea}
          />
          <textarea
            name="careerGoals"
            placeholder="Career Goals"
            value={formData.careerGoals}
            onChange={handleChange}
            style={styles.textarea}
          />

          {/* File uploads */}
          <label style={styles.label}>Upload Profile Image:</label>
          <input
            type="file"
            accept="image/*"
            onChange={handleProfileImageChange}
            style={styles.fileInput}
          />
          <label style={styles.label}>Upload Certificate (PDF):</label>
          <input
            type="file"
            accept=".pdf"
            onChange={handleCertificateFileChange}
            style={styles.fileInput}
          />

          <button type="submit" style={styles.button}>
            Get Career Suggestions
          </button>
        </form>

        {/* Show suggestions & action buttons */}
        {showSuggestions && (
          <>
            <div style={styles.suggestions}>
              <h3>Suggested Career Paths:</h3>
              <ul>
                {careerSuggestions.map((career, idx) => (
                  <li key={idx}>{career}</li>
                ))}
              </ul>
            </div>

            <div style={styles.greenFeatureButtons}>
              <button onClick={generateResume} style={styles.greenBtn}>
                Generate AI Resume
              </button>
              <button onClick={startMockInterview} style={styles.greenBtn}>
                Mock Interview
              </button>
              <button onClick={analyzeSkillGap} style={styles.greenBtn}>
                Skill Gap Analysis
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

// Styles
const styles = {
  page: {
    background: "linear-gradient(to right, #1c92d2, #f2fcfe)",
    minHeight: "100vh",
    padding: "40px 20px",
    color: "#333",
  },
  container: {
    backgroundColor: "#fff",
    padding: "30px",
    borderRadius: "10px",
    maxWidth: "800px",
    margin: "auto",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "12px",
  },
  input: {
    padding: "12px",
    fontSize: "16px",
    borderRadius: "6px",
    border: "1px solid #ccc",
  },
  textarea: {
    minHeight: "60px",
    padding: "12px",
    fontSize: "16px",
    borderRadius: "6px",
    border: "1px solid #ccc",
    resize: "vertical",
  },
  label: {
    marginTop: "10px",
    fontWeight: "600",
  },
  fileInput: {
    padding: "6px 0",
  },
  button: {
    padding: "14px",
    backgroundColor: "#2563eb",
    color: "#fff",
    fontWeight: "bold",
    fontSize: "16px",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    marginTop: "20px",
  },
  suggestions: {
    marginTop: "30px",
    background: "#f1f5f9",
    padding: "20px",
    borderRadius: "8px",
  },
  greenFeatureButtons: {
    display: "flex",
    gap: "15px",
    justifyContent: "center",
    marginTop: "20px",
    flexWrap: "wrap",
  },
  greenBtn: {
    padding: "10px 20px",
    backgroundColor: "#22c55e",
    color: "#fff",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    fontWeight: "600",
  },
};

export default Dashboard;
