import React, { useState } from "react";
import html2pdf from "html2pdf.js";

const ResumeBuilder = () => {
  const [form, setForm] = useState({
    name: "",
    title: "",
    contact: "",
    about: "",
    skills: "",
    education: "",
    experience: "",
    projects: "",
    references: "",
  });

  const [photo, setPhoto] = useState("");
  const [showResume, setShowResume] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.id]: e.target.value });
  };

  const handlePhotoUpload = (e) => {
    const file = e.target.files[0];
    if (!file.type.startsWith("image")) return alert("Invalid file type");

    const reader = new FileReader();
    reader.onload = (ev) => setPhoto(ev.target.result);
    reader.readAsDataURL(file);
  };

  const generateResume = () => {
    for (const key in form) {
      if (!form[key] && key !== "references") {
        alert(`Please fill in the ${key}`);
        return;
      }
    }

    setShowResume(true);
  };

  const downloadPDF = () => {
    const resume = document.getElementById("resume");
    html2pdf().from(resume).save("resume.pdf");
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>🎓 Resume Generator</h2>
      <input type="file" accept="image/*" onChange={handlePhotoUpload} /><br />
      {Object.keys(form).map((key) => (
        <div key={key}>
          <label>{key.charAt(0).toUpperCase() + key.slice(1)}:</label><br />
          {key === "about" || key === "education" || key === "experience" || key === "projects" ? (
            <textarea id={key} value={form[key]} onChange={handleChange} />
          ) : (
            <input id={key} value={form[key]} onChange={handleChange} />
          )}
          <br />
        </div>
      ))}
      <button onClick={generateResume}>Generate</button>
      <button onClick={downloadPDF} disabled={!showResume}>Download PDF</button>

      {showResume && (
        <div id="resume" style={{ marginTop: "20px", padding: "20px", border: "1px solid #000", maxWidth: "800px" }}>
          <img src={photo || "https://via.placeholder.com/100"} alt="Profile" width="100" />
          <h2>{form.name}</h2>
          <p><strong>Title:</strong> {form.title}</p>
          <p><strong>Contact:</strong> {form.contact}</p>
          <p><strong>About:</strong> {form.about}</p>
          <p><strong>Skills:</strong> {form.skills}</p>
          <p><strong>Education:</strong> {form.education}</p>
          <p><strong>Experience:</strong> {form.experience}</p>
          <p><strong>Projects:</strong> {form.projects}</p>
          <p><strong>References:</strong> {form.references}</p>
        </div>
      )}
    </div>
  );
};

export default ResumeBuilder;
