import React, { useState } from "react";

const Dashboard = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    degree: "",
    university: "",
    graduationYear: "",
    skills: "",
    interests: "",
    experience: "",
    certifications: "",
    careerGoals: "",
  });

  const [profileImage, setProfileImage] = useState(null);
  const [certificateFile, setCertificateFile] = useState(null);
  const [careerSuggestions, setCareerSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleProfileImageChange = (e) => {
    setProfileImage(e.target.files[0]);
  };

  const handleCertificateFileChange = (e) => {
    setCertificateFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare form data for backend (with files)
    const data = new FormData();

    // Append text fields
    Object.entries(formData).forEach(([key, value]) => {
      data.append(key, value);
    });

    // Append files if selected
    if (profileImage) data.append("profileImage", profileImage);
    if (certificateFile) data.append("certificateFile", certificateFile);

    // Example: Send to backend (replace URL with your backend API)
    /*
    const response = await fetch("/api/career-suggestions", {
      method: "POST",
      body: data,
    });
    const result = await response.json();
    setCareerSuggestions(result.suggestions);
    setShowSuggestions(true);
    */

    // For now, simulate response
    setCareerSuggestions([
      "Software Developer",
      "AI/ML Engineer",
      "Data Scientist",
      "Product Manager",
    ]);
    setShowSuggestions(true);
  };

  return (
    <div style={styles.page}>
      <div style={styles.container}>
        <h2>Career Guidance & Resume Enhancer Dashboard</h2>
        <p>Fill your details to get personalized career suggestions and resume tips.</p>

        <form onSubmit={handleSubmit} style={styles.form} encType="multipart/form-data">
          {/* Text Inputs */}
          <input
            type="text"
            name="fullName"
            placeholder="Full Name"
            value={formData.fullName}
            onChange={handleChange}
            required
            style={styles.input}
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
            style={styles.input}
          />
          <input
            type="text"
            name="degree"
            placeholder="Degree (e.g., B.Tech, MBA)"
            value={formData.degree}
            onChange={handleChange}
            required
            style={styles.input}
          />
          <input
            type="text"
            name="university"
            placeholder="University / College"
            value={formData.university}
            onChange={handleChange}
            required
            style={styles.input}
          />
          <input
            type="number"
            name="graduationYear"
            placeholder="Graduation Year (e.g., 2025)"
            value={formData.graduationYear}
            onChange={handleChange}
            required
            style={styles.input}
          />
          <textarea
            name="skills"
            placeholder="Skills (e.g., Python, React, SQL)"
            value={formData.skills}
            onChange={handleChange}
            required
            style={styles.textarea}
          />
          <textarea
            name="interests"
            placeholder="Interests (e.g., AI, Data Science)"
            value={formData.interests}
            onChange={handleChange}
            required
            style={styles.textarea}
          />
          <textarea
            name="experience"
            placeholder="Experience (e.g., Internships, projects)"
            value={formData.experience}
            onChange={handleChange}
            required
            style={styles.textarea}
          />
          <textarea
            name="certifications"
            placeholder="Certifications (e.g., AWS, Coursera)"
            value={formData.certifications}
            onChange={handleChange}
            style={styles.textarea}
          />
          <textarea
            name="careerGoals"
            placeholder="Career Goals"
            value={formData.careerGoals}
            onChange={handleChange}
            style={styles.textarea}
          />

          {/* File Inputs */}
          <label style={styles.label}>Profile Image (JPEG/PNG):</label>
          <input
            type="file"
            accept="image/*"
            onChange={handleProfileImageChange}
            style={styles.fileInput}
          />

          <label style={styles.label}>Certificate File (PDF):</label>
          <input
            type="file"
            accept="application/pdf"
            onChange={handleCertificateFileChange}
            style={styles.fileInput}
          />

          <button type="submit" style={styles.button}>
            Get Career Suggestions
          </button>
        </form>

        {showSuggestions && (
          <div style={styles.suggestions}>
            <h3>Suggested Career Paths:</h3>
            <ul>
              {careerSuggestions.map((career, index) => (
                <li key={index}>{career}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

const styles = {
  page: {
    background: "linear-gradient(to right, #1c92d2, #f2fcfe)",
    minHeight: "100vh",
    color: "#333",
    padding: "40px 20px",
  },
  container: {
    background: "#fff",
    padding: "30px",
    borderRadius: "10px",
    maxWidth: "700px",
    margin: "auto",
    boxShadow: "0 8px 20px rgba(0,0,0,0.1)",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "15px",
    marginTop: "20px",
  },
  input: {
    padding: "12px",
    fontSize: "16px",
    borderRadius: "5px",
    border: "1px solid #ccc",
  },
  textarea: {
    minHeight: "70px",
    padding: "12px",
    fontSize: "16px",
    borderRadius: "5px",
    border: "1px solid #ccc",
    resize: "vertical",
  },
  label: {
    fontWeight: "600",
    marginTop: "10px",
  },
  fileInput: {
    padding: "6px 0",
  },
  button: {
    padding: "14px",
    backgroundColor: "#1c92d2",
    color: "#fff",
    fontWeight: "bold",
    fontSize: "18px",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    marginTop: "15px",
  },
  suggestions: {
    marginTop: "30px",
    backgroundColor: "#f9f9f9",
    padding: "20px",
    borderRadius: "8px",
  },
};

export default Dashboard;
